INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Programación en Python - Desde Principiante hasta Experto', 'Curso completo que abarca todos los aspectos de la programación en Python. Desde los conceptos más básicos hasta las técnicas avanzadas y aplicaciones prácticas en el mundo real. Aprenderás a desarrollar proyectos Python desde cero y a convertirte en un experto en el lenguaje.', 24000, '2024-01-13', 'https://firebasestorage.googleapis.com/v0/b/fabiocodeacademy.appspot.com/o/CoursesImages%2FPyhton%20Curso.png?alt=media&token=2128f45d-22d9-45da-8db8-9f01e850e08c', 'Programación');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Desarrollo Web con JavaScript - Construye tu Sitio desde Cero', 'Este curso integral te guiará a través del emocionante mundo del desarrollo web con JavaScript. Aprenderás a construir sitios web dinámicos y modernos desde cero. Desde la manipulación del DOM hasta el desarrollo de aplicaciones web interactivas, este curso te proporcionará las habilidades esenciales para destacar en el desarrollo web.', 22500, '2024-01-14', 'https://firebasestorage.googleapis.com/v0/b/fabiocodeacademy.appspot.com/o/CoursesImages%2FJavaScript%20Curso.png?alt=media&token=21e81e2f-b36f-498c-bdb8-4193b49b12f8', 'Programación');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Dominando Java - Curso Completo para Estudiantes de Desarrollo', 'Sumérgete en el universo de Java con este curso completo. Desde los fundamentos hasta las características avanzadas, aprenderás a desarrollar aplicaciones robustas y escalables utilizando Java. Cubriremos temas como programación orientada a objetos, colecciones, excepciones y patrones de diseño. ¡Prepárate para convertirte en un desarrollador Java experto!', 23000, '2024-01-15', 'https://firebasestorage.googleapis.com/v0/b/fabiocodeacademy.appspot.com/o/CoursesImages%2FJava%20Curso.png?alt=media&token=ebf6d868-f1c8-49c5-9fce-7e8edf964ffd', 'Programación');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('C# en Acción - Aprende Programación Orientada a Objetos', 'Descubre el fascinante mundo de C# con este curso dinámico. Desde los conceptos básicos hasta las complejidades de la programación orientada a objetos, dominarás el lenguaje C# mientras construyes aplicaciones prácticas. Obtén las habilidades esenciales para desarrollar software robusto y eficiente en C#.', 16000, '2024-01-16', 'https://firebasestorage.googleapis.com/v0/b/fabiocodeacademy.appspot.com/o/CoursesImages%2FC%23%20Curso.png?alt=media&token=03b112f5-5284-4d86-8481-d3699effb83c', 'Programación');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Ruby para Todos - Desde Novato hasta Rubyista', 'Conviértete en un maestro de Ruby con este curso comprehensivo. Desde los conceptos básicos de sintaxis hasta las técnicas avanzadas de desarrollo, este curso te llevará desde ser un novato hasta convertirte en un Rubyista experto. Explora la elegancia y la versatilidad de Ruby mientras construyes proyectos emocionantes.', 30, '2024-01-17', 'ruby.jpg', 'Programación');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Desarrollo iOS con Swift - Construye Apps para iPhone', 'Entra en el emocionante mundo del desarrollo de iOS con Swift. Aprenderás a construir aplicaciones nativas para iPhone desde cero. Desde la interfaz de usuario hasta la integración de API, este curso te proporcionará las habilidades esenciales para crear aplicaciones iOS atractivas y funcionales.', 25, '2024-01-18', 'swift.jpg', 'Programación');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Kotlin: Desde el Principio a Desarrollador Android Profesional', 'Descubre Kotlin, el lenguaje de programación moderno para el desarrollo Android. Desde los fundamentos hasta las características avanzadas, este curso te preparará para crear aplicaciones Android de alta calidad utilizando Kotlin. Aprenderás las mejores prácticas y técnicas de desarrollo modernas.', 22700, '2024-01-19', 'https://firebasestorage.googleapis.com/v0/b/fabiocodeacademy.appspot.com/o/CoursesImages%2FKotlin%20Curso.png?alt=media&token=3ca7d2c4-2cf7-41ed-97d1-fed3f50305d9', 'Programación');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('HTML y CSS - Diseño Web Moderno desde Cero', 'Sumérgete en el diseño web moderno con HTML y CSS. Este curso te guiará desde los conceptos básicos hasta las técnicas avanzadas de diseño. Aprenderás a construir sitios web responsivos y atractivos mientras dominas las últimas tendencias en diseño web.', 70, '2024-01-20', 'htmlcss.jpg', 'Desarrollo Web');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('ReactJS Avanzado - Construyendo Interfaces Interactivas', 'Conviértete en un experto en ReactJS con este curso avanzado. Aprenderás a construir interfaces de usuario interactivas y dinámicas utilizando ReactJS. Explora conceptos avanzados como el manejo de estados, el enrutamiento y la gestión de formularios para crear aplicaciones web modernas.', 55, '2024-01-21', 'reactjs.jpg', 'Desarrollo Web');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Angular: Desarrollo de Aplicaciones Web Potentes', 'Explora el poder de Angular en este curso completo. Desde la configuración inicial hasta la creación de aplicaciones web potentes, obtendrás las habilidades necesarias para ser un desarrollador Angular eficiente. Aprenderás sobre componentes, servicios, rutas y más.', 45, '2024-01-22', 'angular.jpg', 'Desarrollo Web');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Vue.js desde Cero - Construye Aplicaciones Modernas', 'Descubre la simplicidad y la elegancia de Vue.js. Este curso te llevará desde los fundamentos hasta la creación de aplicaciones web modernas utilizando Vue.js. Aprenderás sobre componentes, directivas y el sistema de estado para construir aplicaciones escalables y eficientes.', 35, '2024-01-23', 'vuejs.jpg', 'Desarrollo Web');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Node.js: Construyendo Aplicaciones Escalables', 'Domina el desarrollo de aplicaciones escalables con Node.js. Aprenderás a construir servidores eficientes y a trabajar con el ecosistema de Node.js. Desde la gestión de paquetes hasta la creación de API RESTful, este curso te preparará para ser un desarrollador Node.js competente.', 30, '2024-01-24', 'nodejs.jpg', 'Desarrollo Web');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Django en Profundidad - Desarrollo Rápido de Aplicaciones Web', 'Explora las profundidades del desarrollo web con Django. Desde la configuración inicial hasta la creación de aplicaciones complejas, este curso te enseñará a desarrollar rápidamente aplicaciones web utilizando el framework Django de Python. Aprenderás sobre modelos, vistas, plantillas y más.', 25, '2024-01-25', 'django.jpg', 'Desarrollo Web');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Laravel Framework: Desarrollo de Aplicaciones Web con PHP Modernas', 'Descubre el framework PHP moderno con Laravel. Este curso te guiará desde los conceptos básicos hasta la creación de aplicaciones PHP modernas y eficientes. Aprenderás sobre migraciones, Eloquent ORM, rutas y controladores.', 17800, '2024-01-26', 'https://firebasestorage.googleapis.com/v0/b/fabiocodeacademy.appspot.com/o/CoursesImages%2FLaravel%20Curso.png?alt=media&token=5b7e1bff-440c-4009-afaa-a90c7eed4425', 'Desarrollo Web');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('MongoDB: Diseño de Bases de Datos NoSQL', 'Adéntrate en el mundo de las bases de datos NoSQL con MongoDB. Aprenderás a diseñar y gestionar bases de datos NoSQL eficientes utilizando MongoDB. Explora la flexibilidad y escalabilidad de MongoDB mientras desarrollas habilidades prácticas de diseño de bases de datos.', 40, '2024-01-27', 'mongodb.jpg', 'Bases de Datos');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('MySQL desde Cero - Fundamentos de Bases de Datos Relacionales', 'Inicia tu viaje en bases de datos relacionales con MySQL. Este curso te llevará desde la instalación hasta la creación y gestión de bases de datos relacionales eficientes con MySQL. Aprenderás sobre consultas SQL, relaciones y optimización de bases de datos.', 35, '2024-01-28', 'mysql.jpg', 'Bases de Datos');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('PostgreSQL: Administración y Desarrollo Avanzado', 'Domina la administración y el desarrollo avanzado con PostgreSQL. Desde la instalación hasta la optimización de consultas, aprenderás a trabajar eficientemente con PostgreSQL. Explora características avanzadas como las funciones almacenadas, los disparadores y la replicación.', 30, '2024-01-29', 'postgresql.jpg', 'Bases de Datos');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Firebase en Acción - Desarrollo de Aplicaciones en la Nube', 'Descubre el potencial de desarrollo en la nube con Firebase. Este curso te enseñará a construir aplicaciones escalables y en tiempo real utilizando Firebase. Aprenderás sobre autenticación, almacenamiento en la nube, y la integración de Firebase en aplicaciones web y móviles.', 25, '2024-01-30', 'firebase.jpg', 'Bases de Datos');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Docker: Despliegue y Contenerización de Aplicaciones', 'Aprende la revolucionaria tecnología de contenerización con Docker. Este curso te guiará desde la instalación hasta el despliegue de aplicaciones en contenedores Docker. Aprenderás sobre la construcción de imágenes, la orquestación de contenedores y la gestión de entornos de desarrollo.', 20, '2024-01-31', 'docker.jpg', 'DevOps');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Kubernetes para Desarrolladores - Orquestación de Contenedores', 'Descubre la orquestación de contenedores con Kubernetes. Este curso te enseñará a desplegar, escalar y gestionar aplicaciones en entornos Kubernetes. Aprenderás sobre servicios, despliegues y prácticas recomendadas para el desarrollo en entornos orquestados.', 15, '2024-02-01', 'kubernetes.jpg', 'DevOps');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Jenkins: Automatización de Despliegues y CI/CD', 'Automatiza tus procesos de desarrollo con Jenkins. Desde la configuración inicial hasta la creación de tuberías de CI/CD, este curso te proporcionará las habilidades esenciales para la automatización de despliegues y la integración continua utilizando Jenkins.', 10, '2024-02-02', 'jenkins.jpg', 'DevOps');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Git: Control de Versiones desde Principiante hasta Experto', 'Domina el control de versiones con Git. Este curso te guiará desde los conceptos básicos hasta las prácticas avanzadas de Git. Aprenderás sobre ramificaciones, fusiones, conflictos y cómo trabajar eficientemente en equipos utilizando Git.', 50, '2024-02-03', 'git.jpg', 'Control de Versiones');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('SVN: Gestión de Versiones en Proyectos de Desarrollo', 'Explora la gestión de versiones en proyectos de desarrollo con SVN. Aprenderás a configurar y utilizar SVN para gestionar eficientemente el código fuente. Desde la creación de repositorios hasta la fusión de ramas, este curso te proporcionará las habilidades necesarias para la gestión de versiones con SVN.', 40, '2024-02-04', 'svn.jpg', 'Control de Versiones');
INSERT INTO courses (name, description, registered_users, publication_date, image, category) VALUES ('Desarrollo Full Stack con MERN Stack', 'Conviértete en un desarrollador Full Stack con el MERN Stack (MongoDB, Express.js, React y Node.js). Este curso te guiará a través de la creación de aplicaciones web modernas y escalables utilizando todas las tecnologías del MERN Stack. Aprenderás sobre la integración completa de las tecnologías y la construcción de aplicaciones eficientes desde el front-end hasta el back-end.', 60, '2024-02-05', 'mern.jpg', 'Desarrollo Web');
